import React, { Component } from 'react'
import '../sass/bookappointment.scss';
import SimpleReactValidator from 'simple-react-validator';

export class BookAppointment extends Component {

    constructor(props) {
        super(props);
        this.validator = new SimpleReactValidator();
        this.state = {
            name: '',
            surname: '',
            email: '',
            phoneNumber: '',
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    
    handleChange(event) {
    this.setState({name: event.target.name});
    this.setState({surname: event.target.surname});
    this.setState({email: event.target.email});
    this.setState({phoneNumber: event.target.phoneNumber});
    }

    handleSubmit(event) {
    alert('A name was submitted: ' + this.state.name);
    if (this.validator.allValid()) {
        alert('You submitted the form and stuff!');
      } else {
        this.validator.showMessages();
        // rerender to show messages for the first time
        // you can use the autoForceUpdate option to do this automatically`
        this.forceUpdate();
      }
    event.preventDefault();
    }

    render() {
        return (
            <div className="booking-app-wrapper">
                <div className="booking-app-title">
                    <h2>Book your appointment</h2>

                    <form onSubmit={this.handleSubmit}>
                        <input type="text" placeholder="First Name" value={this.state.value} onChange={this.handleChange} />
                        <input type="text" placeholder="Last Name" value={this.state.surname} onChange={this.handleChange} />
                        <div className="form-group">
                            <input className="form-control" type="email" placeholder="Email" value={this.state.email} onChange={this.handleChange} />
                            {this.validator.message('email', this.state.email, 'required|email')}
                        </div>
                        <input type="number" placeholder="Phone Number" value={this.state.phoneNumber} onChange={this.handleChange} />

                        <input type="submit" value="Submit" />
                    </form>
                </div>
            </div>
        )
    }
}

export default BookAppointment
