import React, {Component} from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import { setMinutes, setHours } from 'date-fns';
import moment from 'moment'

export class TimePick extends Component {

  constructor(props) {
    super(props)
    this.state = {
      startDate: null,
      monStart: null,
      monStop: null,
      tueStart: null,
      tueStop: null,
      wedStart: null,
      wedStop: null,
      thuStart: null,
      thuStop: null,
      friStart: null,
      friStop: this.props.getWorkingHours[4].endHour,
    }
  }

  handleChange = date => {
    this.setState({
      startDate: date
    });
    console.log(this.props.getWorkingHours)
  };

  render() {
    const { startDate } = this.state;
    const now = moment().toDate();
    return (
      <div>
         <DatePicker 
          selected={startDate}
          onChange={this.handleChange}
          showTimeSelect
          showTimeSelectOnly
           minTime={setHours(setMinutes(now, 0), 0)}
          maxTime={setHours(setMinutes(now, 45), 23)}
          timeIntervals={this.props.serviceDuration}
          disabled={this.props.disabled}
          timeCaption="Time"
          dateFormat="h:mm aa"
          placeholderText="Select Time"
        />
      </div>
    )
  }
}

export default TimePick
