import React, { Component } from 'react'
import '../sass/bookappointment.scss';

export class BookAppointment extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            surname: '',
            email: '',
            phoneNumber: '',
            barbers: [],
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    
    handleChange(event) {
    this.setState({name: event.target.name});
    this.setState({surname: event.target.surname});
    this.setState({email: event.target.email});
    this.setState({phoneNumber: event.target.phoneNumber});
    }

    handleSubmit(event) {
    alert('Appointment submitted')
    event.preventDefault();
    }

    // API GET BARBERS
    componentDidMount() {
        fetch("http://localhost:3004/barbers")
            .then((responseJson) => {
                console.log(responseJson);
            })
            .then(
            (result) => {
              this.setState({
                barbers: result.barbers
              });
            },
          )
      }

    render() {
        const { barbers } = this.state;
        return (
            <div className="booking-app-wrapper">
                <div className="booking-app-title">
                    <h2>Book your appointment</h2>
                </div>

                <form onSubmit={this.handleSubmit}>
                    <div className="input-wrapper">
                        <input type="text" placeholder="First Name" value={this.state.value} onChange={this.handleChange} required/>
                    </div>

                    <div className="input-wrapper">
                        <input type="text" placeholder="Last Name" value={this.state.surname} onChange={this.handleChange} required/>
                    </div>

                    <div className="input-wrapper">
                        <input placeholder="Email" value={this.state.email} onChange={this.handleChange} required />
                    </div>

                    <div className="input-wrapper">
                       <select>
                           {barbers.map(barber => (
                                <option key={barber.id}>
                                {barber.firstName} {barber.lastName}
                                </option>
                            ))}
                       </select>
                    </div>

                    <div className="input-wrapper">
                        <input type="number" placeholder="Phone Number" value={this.state.phoneNumber} onChange={this.handleChange} required/>
                    </div>

                    <input type="submit" value="Submit" />
                </form>
            </div>
        )
    }
}

export default BookAppointment
