import React, {Component} from 'react';
import DatePicker from 'react-datepicker';
import "react-datepicker/dist/react-datepicker.css";
import { setMinutes, setHours, addMonths } from 'date-fns';
import moment from 'moment'

export class TimePicker extends Component {

  constructor(props) {
    super(props)
    this.state = {
      startDate: null,
    }
  }

  handleChange = date => {
    this.setState({
      startDate: date
    });
    /* console.log(this.props.getWorkingHours.map(day => day[2].endHour))
    console.log(this.props.getWorkingHours.map(day => day[2].startHour)) */
  };

  render() {
    const { startDate } = this.state;
    const now = moment().toDate();
    const isWeekday = date => {
    const day = date.getDay();
        return day !== 0 && day !== 6;
    };
    return (
      <div>
        <div className="input-wrapper">
          <DatePicker
              selected={startDate}
              onChange={this.handleChange}
              filterDate={isWeekday}
              minDate={new Date()}
              maxDate={addMonths(new Date(), 5)}
              disabled={this.props.disabled}
              placeholderText="Select Date"
          />
        </div>
        <div className="input-wrapper">
          <DatePicker 
            selected={startDate}
            onChange={this.handleChange}
            showTimeSelect
            showTimeSelectOnly
            minTime={setHours(setMinutes(now, 0), 0)}
            maxTime={setHours(setMinutes(now, 45), 23)}
            timeIntervals={this.props.serviceDuration}
            disabled={this.props.disabled}
            timeCaption="Time"
            dateFormat="h:mm aa"
            placeholderText="Select Time"
          />
        </div>
      </div>
    )
  }
}

export default TimePicker
