import React, { Component } from 'react'
import '../sass/bookingsuccess.scss';

export class AppointmentBooked extends Component {

    componentDidMount() {
        const baseUrl = 'api.giphy.com/v1/gifs/search?api_key=KeTn0RgXZQF8EDkUGgQmSaJYuWPEz5mI&q=barber';

        // GET BARBERS

        fetch(baseUrl + '/barbers')
        .then(getBarbers => {
            return getBarbers.json();
        })
        .then(getBarbers => {
            this.setState({
                barbers: getBarbers
            })
        })
        .catch(err => {
            alert("Failed to load our Barbers" + err)
        });

        // GET SERVICES

        fetch(baseUrl + '/services')
        .then(getServices => {
            return getServices.json();
        })
        .then(getServices => {
            this.setState({
                services: getServices
            })
        })
        .catch(err => {
            alert("Failed to load our Services" + err)
        });

    }

    render() {
        return (
            <div className="section-booking-success">
                <div className="container">

                    <div className="center-wrapper">
                        <h2>Appointment successfully booked</h2>

                        <div className="success-gif-wrapper">

                        </div>
                    </div>

                </div>
            </div>
        )
    }
}

export default AppointmentBooked
