import React, { Component } from 'react'
import '../sass/bookappointment.scss';
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";

export class BookAppointment extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            surname: '',
            email: '',
            phoneNumber: '',
            barbers: [],
            services: [],
            startDate: new Date(),
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    
    handleChange(event, date) {
        this.setState({name: event.target.name});
        this.setState({surname: event.target.surname});
        this.setState({email: event.target.email});
        this.setState({phoneNumber: event.target.phoneNumber});
        this.setState({
            startDate: date
          });
    }

    handleSubmit(event) {
        alert('Appointment submitted')
        event.preventDefault();
    }

    componentDidMount() {
        const baseUrl = 'http://localhost:3004';
        // GET BARBERS
        fetch(baseUrl + '/barbers')
        .then(getBarbers => {
            return getBarbers.json();
        })

        .then(getBarbers => {
            this.setState({
                barbers: getBarbers
            })
            console.log(getBarbers);
        })
        
        .catch(err => {
            alert("Failed to load our Barbers" + err)
        });

        // GET SERVICES
        fetch(baseUrl + '/services')
        .then(getServices => {
            return getServices.json();
        })

        .then(getServices => {
            this.setState({
                services: getServices
            })
            console.log(getServices);
        })
        
        .catch(err => {
            alert("Failed to load our Services" + err)
        });
    }


    render() {
        const { barbers, services } = this.state;
        return (
            <div className="booking-app-wrapper">
                <div className="booking-app-title">
                    <h2>Book your appointment</h2>
                </div>

                <form onSubmit={this.handleSubmit}>
                    <div className="input-wrapper">
                        <input type="text" placeholder="First Name" value={this.state.value} onChange={this.handleChange} required/>
                    </div>

                    <div className="input-wrapper">
                        <input type="text" placeholder="Last Name" value={this.state.surname} onChange={this.handleChange} required/>
                    </div>

                    <div className="input-wrapper">
                        <input placeholder="Email" value={this.state.email} onChange={this.handleChange} required />
                    </div>

                    <div className="input-wrapper">
                        <input type="number" placeholder="Phone Number" value={this.state.phoneNumber} onChange={this.handleChange} required/>
                    </div>

                    <div className="input-wrapper">
                       <select>
                           <option hidden>Select Barber</option>
                           {barbers.map(barber => (
                                <option key={barber.id}>
                                {barber.firstName} {barber.lastName}
                                </option>
                            ))}
                       </select>
                    </div>

                    <div className="input-wrapper">
                       <select>
                           <option hidden>Select Service</option>
                           {services.map(service => (
                                <option key={service.id}>
                                {service.name}
                                </option>
                            ))}
                       </select>
                    </div>

                    <DatePicker
                        placeholderText="Select Date"
                        onChange={this.handleChange}
                    />

                    <DatePicker
                        onChange={this.handleChange}
                        placeholderText="Select Time"
                    />

                    <input type="submit" value="Book appointment" />
                </form>
            </div>
        )
    }
}

export default BookAppointment
